		function ResetCouponMessages()
			{
			o.SetBucket('DISCARD_USED_COUPON_Static_MESSAGES', '');
			o.SetBucket('COUPON_REJECTED_Static_MESSAGES', '');
			o.SetBucket('WAIT_FOR_ATTENDANT_Static_MESSAGES', '');
			}

		
		function PopulateCpnsMsgListBox(listBox, staticMessages)
			{
			var line = '';
			var splitMessages;

			o.ClearList(listBox);
			splitMessages = staticMessages.split('\r\n');

			for( var i = 0; i <splitMessages.length; i++ )
			{
			line = splitMessages[i];
			PopulateScrollList(listBox, line);
			}

			o.ScrollTo(listBox, 0);
			o.SelectIndexOfCurrentPage(listBox, 0);
			}

		function TrimZero(str)
			{ 
			if (str != null) 
				{
				var i;
				for (i=0; i < str.length; i++) 
					{
					if (str.charAt(i)=='0')
						{
						str=str.substr(i);
						i=0;
						}
					else
						{
						str=str.substr(i);
						break;
						}
					}
				}
			return str;
			}

		var maxCountries = 10;
		var maxTowns = 100;

		
		function ResetZipCodeInfo()
			{
			o.ClearList('LB_TOWNS');
			o.SetBucket('CONFIRM_TOWN_Static_CONFIRM_TOWN_POSTAL_CODE', '');
			o.SetBucket('CONFIRM_TOWN_Static_CONFIRM_TOWN_NAME', '');
			o.SetBucket('CONFIRM_UNKNOWN_POSTAL_CODE_Static_POSTAL_CODE', '');
			o.SetBucket('CHOOSE_TOWN_Static_POSTAL_CODE', '');
			o.SetBucket('ENTER_POSTAL_CODE_Static_DEFAULT_COUNTRY', '');

			for (var i = 1; i <= maxTowns; i++)
				if (o.Bucket('CHOOSE_TOWN_Button_Town' + i) != null)
					o.SetBucket('CHOOSE_TOWN_Button_Town' + i, '');							

			for (var i = 1; i <= maxCountries; i++)
				if (o.Bucket('ENTER_POSTAL_CODE_Static_COUNTRY' + i) != null)
					o.SetBucket('ENTER_POSTAL_CODE_Static_COUNTRY' + i, '');
			}

		
		function GetCountryList()
			{
			var countriesEN = new Array('France', 'Belgium', 'Luxembourg', 'Germany', 'Switzerland', 'Italy', 'Spain', 'United Kingdom', 'Monaco', 'Other country');
			var countriesFR = new Array('France', 'Belgique', 'Luxembourg', 'Allemagne', 'Suisse', 'Italie', 'Espagne', 'Royaume Uni', 'Monaco', 'Autre pays');
			var codes = new Array('000', '001', '002', '003', '004', '005', '006', '007', '98000', '99999');
			var lenCodes = codes.length;
			var lenCountriesEN = countriesEN.length;
			var lenCountriesFR = countriesFR.length;
			var codeDefault = o.Bucket('ENTER_POSTAL_CODE_Static_DEFAULT_COUNTRY');
			var posDefault = -1;
			var locDefault = -1;

			if( lenCodes == lenCountriesEN && lenCodes == lenCountriesFR )
				{
				for( var i = 1; i <= maxCountries; i++ )
					{
					var temp_code = Pick(0, o.Bucket('ENTER_POSTAL_CODE_Static_COUNTRY' + i), '/');
					var temp_flag = Pick(1, o.Bucket('ENTER_POSTAL_CODE_Static_COUNTRY' + i), '/');

					var pos = -1;

					for( var j = 0; j < lenCodes; j++ )
						{
						if( codes[j] == codeDefault )
							posDefault = j;

						if( codes[j] == temp_code && temp_flag != -1 )
							{
							pos = j;
							break;
							}
						}

					if( pos != -1 )
						{
						o.SetElement('Country' + i, countriesEN[pos]);
						o.SetElementPropertyValue('BTN_COUNTRY_' + i, 'Picture', 'BTN_COUNTRY_' + o.Element('Country' + i) + '.png');
						o.SetElementPropertyValue('BTN_COUNTRY_' + i, 'Enable', 'True');
						o.SetElementPropertyValue('imgCountry_' + i, 'Picture', 'PIC_COUNTRY_' + o.Element('Country' + i) + '.png');
						o.SetElement('Country' + i + '_ShowPinpad', temp_flag);
						o.SetElement('txtCountry_' + i + '_EN', o.Element('Country' + i));
						o.SetElement('txtCountry_' + i + '_FR', countriesFR[pos]);
						o.SetElement('Country' + i + '_Code', temp_code);

						if ( posDefault == pos )
							locDefault = i;
						}
					else
						{
						o.SetElement('Country' + i, '');
						o.SetElementPropertyValue('BTN_COUNTRY_' + i, 'Picture', 'PIC_BLANK.png');
						o.SetElementPropertyValue('BTN_COUNTRY_' + i, 'Enable', 'False');
						o.SetElementPropertyValue('imgCountry_' + i, 'Picture', 'PIC_BLANK.png');
						o.SetElement('Country' + i + '_ShowPinpad', 0);
						o.SetElement('txtCountry_' + i + '_EN', '');
						o.SetElement('txtCountry_' + i + '_FR', '');
						o.SetElement('Country' + i + '_Code', '');
						}
					}

				o.SetElement('SelectedCountry', countriesEN[posDefault]);
				o.SetElement('SelectedCountryFR', countriesFR[posDefault]);							
				o.SetElement('ShowPinpad', o.Element('Country' + locDefault + '_ShowPinpad'));
				o.SetElement('SelectedCountryCode', codeDefault);
				}
			}

		
		function PopulateTownListBox()
			{
			var otherTownNameFR = 'Autre commune';
			var otherTownNameEN = 'Other town';

			o.ClearList('LB_TOWNS');

			for( var i = 1; i <= maxTowns; i++ )
				{
				if (o.Bucket('CHOOSE_TOWN_Button_Town' + i) != null)
					{
					var tempTownName = Pick(0, o.Bucket('CHOOSE_TOWN_Button_Town' + i), '/');
					var tempTownCode = Pick(1, o.Bucket('CHOOSE_TOWN_Button_Town' + i), '/');

					if (tempTownName != otherTownNameFR)
						{
						PopulateScrollList('LB_TOWNS', tempTownName, '0', '1', tempTownCode);
						}
					else
						{
						PopulateScrollList('LB_TOWNS', o.Language == 'English' ? otherTownNameEN : tempTownName, '0', '1', tempTownCode);
						break;
						}
					}
				else
					break;
				}
				o.ScrollTo('LB_TOWNS', 0);
				o.SelectIndexOfCurrentPage('LB_TOWNS', 0);
			}

		
		var MaxNumDenoms = 8;

		function LoadRecyclerElements( RecyclerModule, Type )
			{
			if (Type == 'Bill')
				{
				if (RecyclerModule == 'LOADER')
					{
					for( var i = 1; i <= MaxNumDenoms; i++ )
						{
						o.SetElement('BillDenom' + i, Pick(i-1, o.GetRegPath('Devices\\\\CashRecycler', 'LoaderDenominations'), ','));
						o.SetElement('RecyclerDenom' + i, '€' + o.Element('BillDenom' + i));
						o.SetElementPropertyValue('BillLevel' + i, 'DenomValue', o.Element('BillDenom' + i));
						o.SetElementPropertyValue('BillLevel' + i, 'Thresholds', 'Devices\\\\CashManager\\\\BillLoader');
						}
					}
				else if (RecyclerModule == 'CASHBOX')
					{
					for( var i = 1; i <= MaxNumDenoms; i++ )
						{
						o.SetElement('BillDenom' + i, Pick(i-1, o.GetRegPath('Devices\\\\CashRecycler', 'CashboxDenominations'), ','));
						o.SetElement('RecyclerDenom' + i, '€' + o.Element('BillDenom' + i));
						o.SetElementPropertyValue('BillLevel' + i, 'DenomValue', o.Element('BillDenom' + i));
						o.SetElementPropertyValue('BillLevel' + i, 'Thresholds', 'Devices\\\\CashManager\\\\BillCashBox');
						}
					}
				else if (RecyclerModule == 'RECYCLER')
					{
					for( var i = 1; i <= MaxNumDenoms; i++ )
						{
						o.SetElement('BillDenom' + i, Pick(i-1, o.GetRegPath('Devices\\\\CashRecycler', 'RecyclerDenominations'), ','));
						o.SetElement('RecyclerDenom' + i, '€' + o.Element('BillDenom' + i));
						o.SetElementPropertyValue('BillLevel' + i, 'DenomValue', o.Element('BillDenom' + i));
						o.SetElementPropertyValue('BillLevel' + i, 'Thresholds', 'Devices\\\\\CashManager\\\BillRecycler');
						}
					}
				else if (RecyclerModule == 'TOTAL')
					{
					for( var i = 1; i <= MaxNumDenoms; i++ )
						{
						o.SetElement('BillDenom' + i, Pick(i-1, o.GetRegPath('Devices\\\\CashRecycler', 'TotalDenominations'), ','));
						o.SetElement('RecyclerDenom' + i, '€' + o.Element('BillDenom' + i));
						o.SetElementPropertyValue('BillLevel' + i, 'DenomValue', o.Element('BillDenom' + i));
						o.SetElementPropertyValue('BillLevel' + i, 'Thresholds', 'Devices\\\\CashManager\\\\BillTotal');
						}
					}
				}
			else
				{
				if (RecyclerModule == 'RECYCLER')
					{
					for( var i = 1; i <= MaxNumDenoms; i++ )
						{
						o.SetElement('CoinDenom' + i, Pick(i-1, o.GetRegPath('Devices\\\\CashRecycler\\\\CoinRecycler','RecycledCoinDenominations'), ','));
						o.SetElement('CoinRecyclerDenom' + i, '€' + (o.Element('CoinDenom' + i) / 100).toFixed(2));
						o.SetElementPropertyValue('CoinLevel' + i, 'DenomValue', o.Element('CoinDenom' + i));
						o.SetElementPropertyValue('CoinLevel' + i, 'Thresholds', 'Devices\\\\CashManager\\\\CoinRecycler' + o.Element('CoinDenom' + i));
						}
					}
				else if (RecyclerModule == 'OVERFLOW')
					{
					for( var i = 1; i <= MaxNumDenoms; i++ )
						{
						o.SetElement('CoinDenom' + i, Pick(i-1, o.GetRegPath('Devices\\\\CashRecycler\\\\CoinRecycler','AcceptedCoinDenominations'), ','));
						o.SetElement('CoinRecyclerDenom' + i, '€' + (o.Element('CoinDenom' + i) / 100).toFixed(2));
						o.SetElementPropertyValue('CoinLevel' + i, 'DenomValue', o.Element('CoinDenom' + i));
						o.SetElementPropertyValue('CoinLevel' + i, 'Thresholds', 'Devices\\\\CashManager\\\\CoinOverflow');
						}
					}
				else if (RecyclerModule == 'TOTAL')
					{
					for( var i = 1; i <= MaxNumDenoms; i++ )
						{
						o.SetElement('CoinDenom' + i, Pick(i-1, o.GetRegPath('Devices\\\\CashRecycler\\\\CoinRecycler','AcceptedCoinDenominations'), ','));
						o.SetElement('CoinRecyclerDenom' + i, '€' + (o.Element('CoinDenom' + i) / 100).toFixed(2));
						o.SetElementPropertyValue('CoinLevel' + i, 'DenomValue', o.Element('CoinDenom' + i));
						o.SetElementPropertyValue('CoinLevel' + i, 'Thresholds', 'Devices\\\\CashManager\\\\CoinTotal' + o.Element('CoinDenom' + i));
						}
					}
				}
			}

		
		function ShowLoaderInfo()
			{
			var sNumLoaderDenoms = (o.GetRegPath('Devices\\\\CashRecycler','LoaderDenominations')).length;
			for( var i = 1; i <= sNumLoaderDenoms; i++ )
				{
				o.SetElement('BillDenom' + i, Pick(i-1, o.GetRegPath('Devices\\\\CashRecycler','LoaderDenominations'), ','));
				o.SetElement('txtLoaderDenom' + i, '€' + o.Element('BillDenom' + i));
				o.SetElement('txtRecyclerSetNumBills' + i, (o.Bucket('NUM_BILLS_' + o.Element('BillDenom' + i))/1).toFixed(0));
				}
			}
